 import api from "../services/api";
export const FETCH_ALL = 'FETCH_ALL'
export const fetchAll = () => dispatch => {

     api.get(`v1/users`)
         .then(res => {
             dispatch({
                 type: FETCH_ALL,
                 payload: res.data
             })
         })
         .catch(err => console.log(err))

   }
