import React from "react";
import { useForm } from "react-hook-form";
import { connect, useDispatch } from "react-redux";
import { Link, useHistory, Redirect } from "react-router-dom";
import { signUp } from "../../actions/authActions";
import PropTypes from "prop-types";



const Register = ({  signUp, isLoggedIn }) => {
	const [formData, setFormData] = useForm({
		name: "",
		email: "",
		password: "",
	});
	


	const { name, email, password } = formData;
	const history = useHistory();
	const dispatch = useDispatch();
	const onChange = (e) =>
		setFormData({ ...formData, [e.target.name]: e.target.value });
	
	const onSubmit = async (e) => {
		console.log("Form data", e);
		e.preventDefault();
	 
		dispatch(signUp(formData,history));
			 signUp({ name, email, password });
			
	};

	// Redirect if logged in
	if (isLoggedIn) {
		return <Redirect to="/login" />;
	}

	return (
		<div className="register-form">
			<h1 className="heading">Sign Up</h1>
			<p className="lead">
				<i className="fas fa-user"></i> Create Your Account
			</p>
			{/* <Alert /> */}
			<br />
			<form className="form" onSubmit={(e) => onSubmit(e)}>
				<div className="form-group">
					<input
						type="text"
						placeholder="Name"
						name="name"
						{...formData("name", { required: true, maxLength: 20 })}
						onChange={(e) => onChange(e)}
					/>
					 
				</div>
				<div className="form-group">
					<input
						type="email"
						placeholder="Email Address"
						name="email"
						{...formData("email", { required: true, maxLength: 20 })}
						onChange={(e) => onChange(e)}
					/>
					
				</div>
				<div className="form-group">
					<input
						type="password"
						placeholder="Password"
						name="password"
						minLength="6"
						value={password}
						{...formData("password", { required: true, maxLength: 20 })}
						onChange={(e) => onChange(e)}
					/>
					
				</div>
				
				<button type="submit" className="btn btn-primary" href="/login">Register</button>
				
			</form>
			<p className="link">
				Already have an account? <Link to="/login">Sign In</Link>
			</p>
		</div>
	);
};

Register.propTypes = {
	signUp: PropTypes.func.isRequired,
	isLoggedIn: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isLoggedIn: state.auth.isLoggedIn,
});

export default connect(mapStateToProps, {  signUp })(Register);
