import { RECEIVE_ARTICLE } from '../actions/user';

export default function articleReducer(state = {}, action) {
  switch (action.type) {
    case RECEIVE_ARTICLE:
      return action.article;
    // case UPDATE_ARTICLE:
    //   return {
    //     id: action.id,
    //     title: action.payload.title,
    //     content: action.payload.content,
    //   }
    default:
      return state;
  }
};
