import React, { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { useSelector, useDispatch } from "react-redux";
// import {  } from "react-redux";
import { getUserById, updateUserById } from "../actions/user";

const Update = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const history = useHistory();
  const { user } = useSelector((state) => state.users?.results);

  const currentContact = user.find((res) => res._id === id);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("");

  useEffect((_id) => {
    getUserById(id);
  }, [dispatch, id]);

  useEffect(() => {
    if (currentContact) {
      setName(currentContact.name);
      setEmail(currentContact.email);
      setRole(currentContact.role);
    }
  }, [currentContact]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const checkEmail = user.find(
      (res) => res._id !== id && res.email === email && res
    );

    const checkPhone = user.find(
      (res) => res._id !== id && res.role === role && res
    );

    if (!email || !name || !role) {
      return toast.warning("Please fill in all fields!!");
    }

    if (checkEmail) {
      return toast.error("This email already exists!!");
    }
    if (checkPhone) {
      return toast.error("This phone number already exists!!");
    }

   const res = {
    _id:id,
    name,
    email,
    role
   } 
  

    dispatch(updateUserById(res));
    toast.success("Contact updated successfully!!");
    history("/dashboard");
  };

  return (
    <div className="container">
      {currentContact ? (
      <div className="row d-flex flex-column">
        <button
          className="btn btn-dark ml-auto my-5"
          onClick={() => history("/dashboard")}
        >
          Go back
        </button>
        <h3 style={{ textAlign: "center" }}>EDIT STUDENT {id} </h3>
        <div className="col-md-6 mx-auto shadow p-5">
          <form key={id} onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                className="form-control"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                className="form-control"
                placeholder="Role"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              />
            </div>
            <div className="form-group d-flex align-items-center justify-content-between my-2">
              <button
                type="submit"
                className="btn btn-primary"
                onClick={() => handleSubmit}
              >
                Update Contact
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => history("/dashboard")}
              >
                cancel
              </button>
            </div>
          </form>
        </div>
      </div>
      ) : (
        <h1 className="text-center">No Contact {id} Found</h1>
      )}
    </div>
  );
};

export default Update;
