import * as actionTypes from "./actionTypes";

// SignUp action creators

export const userSignupRequest = () => {
  return {
    type: actionTypes.USER_SIGNUP_REQUEST,
  };
};
export const userSignupSuccess = (user) => {
  return {
    type: actionTypes.USER_SIGNUP_SUCCESS,
    payload: user,
  };
};
export const userSignupFailure = (error) => {
  return {
    type: actionTypes.USER_SIGNUP_FAILURE,
    payload: error,
  };
};

// Login action creators

export const userLoginRequest = () => {
  return {
    type: actionTypes.USER_LOGIN_REQUEST,
  };
};
export const userLoginSuccess = (user) => {
  return {
    type: actionTypes.USER_LOGIN_SUCCESS,
    payload: user,
  };
};
export const userLoginFailure = (error) => {
  return {
    type: actionTypes.USER_LOGIN_FAILURE,
    payload: error,
  };
};

// loadUser action creators

export const loadUserRequest = () => {
  return {
    type: actionTypes.USER_RELOAD_REQUEST,
  };
};
export const loadUserSuccess = (token) => {
  return {
    type: actionTypes.USER_RELOAD_SUCCESS,
    payload: token,
  };
};
export const loadUserFailure = (error) => {
  return {
    type: actionTypes.USER_RELOAD_FAILURE,
    payload: error,
  };
};

// Logout action creators

export const userLogout = () => {
  return {
    type: actionTypes.USER_LOGOUT,
  };
};

export const addFavoriteTerm =  (user) => {
  return {
    type: ADD_FAVORITE_TERM,
    payload: {
      name: user.name,
      email: user.email,
      role: user.role,
    }
  }
};

export const removeFavoriteTerm = name => {
  return {
    type: REMOVE_FAVORITE_TERM,
    payload: {
      name
    }
  }
}



