import { FETCH_GITHUB_DATA } from '../actions/actionTypes';

const initialState = {

    isLoggedIn: !!localStorage.getItem("token"),
    data: {
      id: "",
      name: "",
      email: "",
      token: "",
      role: "",
    },
    data: [],
    error: "",
  };



export default function githubReducer(state = initialState, action) {
  switch (action.type) {
      case FETCH_GITHUB_DATA:
      return action.data;
    default:
      return state;
  }
}
