import React, { Component } from 'react';
import UserListing from '../components/CustomerList';
import UpdateDetails from '../components/UpdateUser';
import ViewDetails from '../components/DetailsComponent';
import AddUser from '../components/AddUserComponent';
import {Router, Route, NavLink, Switch} from 'react-router-dom'
import history from '../history';

class App extends Component {
  render() {
    return (
      <Router history={history}>
        <div className="container">
          <Navigation />
          <Main />
        </div>
      </Router>
    );
  }
}

const Navigation = () => (
  <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
    <ul className="navbar-nav mr-auto">
      <li className="nav-item"><NavLink exact className="nav-link" activeClassName="active" to="/listing">Articles</NavLink></li>
      <li className="nav-item"><NavLink exact className="nav-link" activeClassName="active" to="/add">Add Article</NavLink></li>
    </ul>
  </nav>
);

const Main = () => (
  <Switch>
    {/* <Route exact path="/dashboard" component={ArticleList} /> */}
    {/* <Route exact path="/articles" component={ArticleList} />
    <Route exact path="/articles/new" component={ArticleAdd} />
    <Route exact path="/articles/:id" component={ArticleInfo} />
    <Route exact path="/articles/:id/edit" component={ArticleEdit} /> */}

<Route exact path={["/listing"]} component={UserListing} />
      <Route exact path={["/update/:id"]} component={UpdateDetails} />
      <Route exact path={["/details/:id"]} component={ViewDetails} />
      <Route exact path={["/add"]} component={AddUser} />
  </Switch>
);

export default App;
