import React, { useState, useEffect } from "react";
import { connect, useDispatch } from "react-redux";
import { Link, useHistory, Redirect } from "react-router-dom";
// import { register } from "../../actions/auth";
import { signUp } from "../../actions/authActions";
import PropTypes from "prop-types";


// import Alert from "../layout/Alert";
// import { setAlert } from "../../actions/alert";

const Register = ({  signUp, isLoggedIn }) => {
	const [formData, setFormData] = useState({
		name: "",
		email: "",
		password: "",
	});
	if (isLoggedIn) {
		return <Redirect to="/login" />;
	}

	const [validation, setValidation] = useState({
		name: "",
		email: "",
		password: "",
	  });

	const { name, email, password } = formData;
	const history = useHistory();
	const dispatch = useDispatch();
	const onChange = (e) =>
		setFormData({ ...formData, [e.target.name]: e.target.value });
	
	const onSubmit = async (e) => {
		console.log("Form data", e);
		e.preventDefault();
		
		dispatch(signUp(formData,history));
			 signUp({ name, email, password });
		
	};

	const checkValidation = () => {
		const errors = validation;
	
		
		if (!formData.name.trim()) {
		  errors.name = "name is required";
		} else {
		  errors.name = "";
		}
		
		const emailCond =
		  "/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
		if (!formData.email.trim()) {
		  errors.email = "Email is required";
		} else if (!formData.email.match(emailCond)) {
		  errors.email = "Please ingress a valid email address";
		} else {
		  errors.email = "";
		}
	
		
		const cond1 = "/^(?=.*[a-z]).{6,20}$/";
		const cond2 = "/^(?=.*[A-Z]).{6,20}$/";
		const cond3 = "/^(?=.*[0-9]).{6,20}$/";
		const password = formData.password;
		if (!password) {
		  errors.password = "password is required";
		} else if (password.length < 6) {
		  errors.password = "Password must be longer than 6 characters";
		} else if (password.length >= 20) {
		  errors.password = "Password must shorter than 20 characters";
		} else if (!password.match(cond1)) {
		  errors.password = "Password must contain at least one lowercase";
		} else if (!password.match(cond2)) {
		  errors.password = "Password must contain at least one capital letter";
		} else if (!password.match(cond3)) {
		  errors.password = "Password must contain at least a number";
		} else {
		  errors.password = "";
		}
		setValidation(errors);
	}	


	useEffect(() => {
		checkValidation();
	  }, [formData]);

	// Redirect if logged in
	if (isLoggedIn) {
		return <Redirect to="/login" />;
	}

	return (
		<div className="register-form">
			<h1 className="heading">Sign Up</h1>
			<p className="lead">
				<i className="fas fa-user"></i> Create Your Account
			</p>
			{/* <Alert /> */}
			<br />
			<form className="form" onSubmit={(e) => onSubmit(e)}>
				<div className="form-group">
					<input
						type="text"
						placeholder="Name"
						name="name"
						value={name}
						onChange={(e) => onChange(e)}
					/>
					 {validation.name && <p>{validation.name}</p>}
            {validation.name && console.log(validation)}
				</div>
				<div className="form-group">
					<input
						type="email"
						placeholder="Email Address"
						name="email"
						value={email}
						onChange={(e) => onChange(e)}
					/>
					{validation.email && <p>{validation.email}</p>}
            {validation.email && console.log(validation)}
				</div>
				<div className="form-group">
					<input
						type="password"
						placeholder="Password"
						name="password"
						minLength="6"
						value={password}
						onChange={(e) => onChange(e)}
					/>
					{validation.password && <p>{validation.password}</p>}
            {validation.password && console.log(validation)}
				</div>
				
				<input type="submit" className="btn btn-primary" href="/login" value="Register"/>
				
			</form>
			<p className="link">
				Already have an account? <Link to="/login">Sign In</Link>
			</p>
		</div>
	);
};

Register.propTypes = {
	// setAlert: PropTypes.func.isRequired,
	signUp: PropTypes.func.isRequired,
	isLoggedIn: PropTypes.bool,
};

const mapStateToProps = (state) => ({
	isLoggedIn: state.auth.isLoggedIn,
});

export default connect(mapStateToProps, {  signUp })(Register);
