  const initialState = {
    isLoggedIn: !!localStorage.getItem("token"),
    loading: false,
    result: "",
    results: [],
    error: "",
  };

  export function users(state = initialState, action) {
    switch (action.type) {

      case GET_USER_REQUEST:
        return {
          ...state,
        };
      case GET_USER_SUCCESS:
        return {
            isLoggedIn: !!localStorage.getItem("token"),
          ...state,
          results: action.payload?.response,
        };
