import {  getUsers, deleteUser, updateUser, getUser, addUser  } from './actions';

import api from "../services/api";


/// add a new user
export const addUserAction = (user) => {
    return (dispatch) => {
        /// axios is a library used to make request to an API, 
        /// return data and manipulate the data .
        api.post('/v1/users', user)
            .then(response => {
                console.log(response);
                dispatch(addUser(response.data))
            })
            .catch(error => {
                console.log("eror", error);
            });
    }
}

/// fetch data of a single user  basedin id
export const getUserAction = (id) => {
    return (dispatch) => {
        api.get(`/v1/users/${id}`)
            .then(response => {
                console.log(response);
                dispatch(getUser(response.data)
                );
            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// fetch all users 
export const getUsersAction = () => {
    return (dispatch) => {
        api.get('v1/users')
            .then(response => {
                console.log(response);
                /// dispatch function dispatches an action which triggers state changes in the store
                dispatch(getUsers(response.data)
                );

            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// delete a user
export const deleteUserAction = (id) => {
    return (dispatch) => {
        api.delete(`/v1/users/${id}`)
            .then(response => {
                console.log(response);
                dispatch(deleteUser());
                dispatch(getUsersAction());
            })
            .catch(error => {
                console.log(error);
            });
    }
}

/// update the existing data of a user
export const updateUserAction = (data, id, history ) => {
    return (dispatch) => {
        api.patch(`/v1/users/${id}`,{

            name: data.name,
            email: data.email,

        })
            .then(response => {
                console.log(response);
                dispatch(updateUser(response.data));
                history.push("/dashboard");
                dispatch(getUsersAction());

            })
            .catch(error => {
                console.log(error);
            });
    }
}
