const initialState = {
    users: [],
    user:null
   };

   export const contactReducer = (state = initialState, action) => {
     switch (action.type) {
      case "FETCH_ALL":
        return {
            ...state,
            users: [...action.payload.data]
        };
        //default
       default:
         return state;
     }
   }
