import {FETCH, FETCH_SUCCESS, FAILED} from "../actions/users";

let initialState = { postList: [] }

 export default function(state=initialState , actions) {

 switch(actions.type) {

     case FETCH_SUCCESS:

            state = Object.assign({}, { postList: [{ user: actions.data }] })
            break;



     default:
         return state;            
 }
 }
