import {FETCH, FETCH_SUCCESS, FAILED} from "../actions/users";

const initialState = {
  state: [],
  data: [],
  status: "",

 }

 export default function(state=initialState , actions) {
    console.log("fulfilled")
    state.status = "idle";
    console.log(actions)
    state.data=actions.payload;
 switch(actions.type) {
     case FETCH:
         return {
             ...state
         }
     case FETCH_SUCCESS:
         return {
             ...state,
             data: actions.payload
         }
     case FAILED:
         return {
             ...state
         }
     default:
         return state;            
 }
 }
