import {FETCH, FETCH_SUCCESS, FAILED} from "../actions/users";

const initialState = {
    data: {},
    loading: false,
    error: null

 }

 export default function(state=initialState , actions) {
    console.log(actions, "sachin")
 switch(actions.type) {
     case FETCH:
         return {
             ...state
         }
     case FETCH_SUCCESS:
         console.log("sachin",actions)
         return {
            ...state,
            loading: false,
            data: actions.data

         }
     case FAILED:
         return {
             ...state
         }
     default:
         return state;            
 }
 }
