        //     <h1>role: {user && user.role}</h1>




		// {/* <Read/> */}
		// </div>    
	)
};
Dashboard.propTypes = {
	auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
	auth: state.auth,
});

export default connect(mapStateToProps)(Dashboard);


