import React, { useEffect, useState } from 'react';

import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getUsersAction, deleteUserAction } from '../actions/creators';
import * as ReactBootstrap from 'react-bootstrap';
import ReactPaginate from 'react-paginate';
import './index.css'
import {
  makeStyles,
  useTheme,
  AppBar,
  Toolbar,
  Typography,
  Button,
  TableContainer,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  useMediaQuery,
} from "@material-ui/core";
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
} from "@material-ui/icons";


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    textAlign: "center",
  },
  button: {
    margin: theme.spacing(1),
  },
  table: {
    flex: 1,
    alignContent: "center",
    justifyContent: "center",
  },
}));

const Read = () => {
  const classes = useStyles();
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));
    const [id, setId] = useState("");
       const [open, setOpen] = useState(false);
    
    const dispatch = useDispatch();
    const [searchTerm, setSearchTerm] = useState("");
    const [loading, setLoading] = useState(false);
    const [pageNumber, setPageNumber] = useState(0);


    const  users  = useSelector(state => state.users?.results);

    const usersPerPage = 7;
    const pagesVisited = pageNumber * usersPerPage;
    const pageCount = Math.ceil(users.length / usersPerPage);

    const changePage = ({ selected }) => {
        setPageNumber(selected);
    }

    const openDialog = (id) => {
          setOpen(true);
          setId(id);
        };
      
        const handleClose = () => {
          setOpen(false);
        };
      
        const confirmDelete = () => {
            
          dispatch(deleteUserAction(id));
        };
      
    useEffect(() => {
        dispatch(getUsersAction(), setLoading(true));
    }, []);

    return (
      <React.Fragment>
      

<AppBar position="static">
        <Toolbar>
           <Typography variant="h6" className={classes.title}>
             ReactJs CRUD App With React Redux Thunk
           </Typography>
         </Toolbar>
       </AppBar>
       <input
                                type="text"
                                className="form-control "
                                placeholder="Search user"
                                onChange={(e) => {
                                    setSearchTerm(e.target.value);
                                }}/>
       <Link to="/create">
      
         <Button
         variant="contained"
         color="primary"
          className={classes.button}
           startIcon={<AddIcon />}
       >
         Create User
        </Button>
      </Link>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
           <TableHead>
             <TableRow>
               <TableCell>S.No</TableCell>
               <TableCell>Name</TableCell>
               <TableCell>Email</TableCell>
               <TableCell>Role</TableCell>
               <TableCell>Update</TableCell>
               <TableCell>Delete</TableCell>
             </TableRow>
           </TableHead>
           <TableBody>
           {loading ?
               users.slice(pagesVisited, pagesVisited + usersPerPage).filter(val => {
                 if (searchTerm === "") {
                   return val;
                  } else if (
                     val.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                      val.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                      val.role.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                      return val;
                   }
                       return false;           
                   }).map((user, index) => 
              <TableRow key={index}>
                <TableCell component="th" scope="row">
                  {index + 1}
                </TableCell>
                <TableCell>{user.name}</TableCell>
                <TableCell>{user.email}</TableCell>
                <TableCell>{user.role}</TableCell>
                <TableCell>
                  <Link to={`/update/${user.id}`}>
                    <EditIcon>edit</EditIcon>
                  </Link>
                </TableCell>
                <TableCell>
                  <DeleteIcon onClick={() => openDialog(user.id)}>
                    delete
                  </DeleteIcon>
                </TableCell>
              </TableRow>
           ) :
           <ReactBootstrap.Spinner animation="border" variant="primary" />
       }
          </TableBody>
        </Table>
       
        <ReactPaginate
                        previousLabel={"Previous"}
                        nextLabel={"Next"}
                        pageCount={pageCount}
                        onPageChange={changePage}
                        containerClassName={"paginationBttns"}
                        previousLinkClassName={"previousBttns"}
                        nextLinkClassName={"nextBttn"}
                        disabledClassName={"paginationDisabled"}
                        activeClassName={"paginationActive"}

                    />
                     
      </TableContainer>



      {open && (
         <Dialog
           fullScreen={fullScreen}
           open={open}
           onClose={handleClose}
           aria-labelledby="Delete User"
         >
           <DialogContent style={{ width: 300 }}>
             <DialogContentText>Are you sure?</DialogContentText>
           </DialogContent>
           <DialogActions>
             <Button autoFocus onClick={handleClose} color="primary">
               Cancel
             </Button>
             <Button onClick={confirmDelete}   href='/dashboard' color="primary" autoFocus>
               Delete
             </Button>
           </DialogActions>
         </Dialog>
       )}
      
     </React.Fragment>
      
   )

     }
    
export default Read;
