import { ADD_FETCHED_DATA, ADD_FAVORITE_TERM, REMOVE_FAVORITE_TERM  } from './usersActiontypes';
import api from "../services/api";
export const addFavoriteTerm =  (data) => {
  return {
    type: ADD_FAVORITE_TERM,
    payload: {
      name: data.name,
      email: data.email,
      role: data.role
    }
  }
};

export const removeFavoriteTerm = name => {
  return {
    type: REMOVE_FAVORITE_TERM,
    payload: {
      name
    }
  }
}

export const fetchData = () => {
  return (dispatch) => {
      return api.get('/v1/users')
          .then(response => {
              return response.data
          })
          .then(data => {
              dispatch({
                  type: ADD_FETCHED_DATA,
                  payload: data
              })
          })
          .catch(error => {
              throw (error);
          });
  };
};
